-- --------------------------------------------------------
-- Update rating cache columns
-- (Run this after inserting/modifying comments with ratings)
-- --------------------------------------------------------

UPDATE `vlog_posts` vp
LEFT JOIN (
  SELECT
    post_id,
    COUNT(rating) AS rating_count,
    AVG(rating) AS avg_rating
  FROM `vlog_comments`
  WHERE rating IS NOT NULL AND rating > 0 AND is_approved = 1
  GROUP BY post_id
) vc_stats ON vp.id = vc_stats.post_id
SET
  vp.no_of_ratings = IFNULL(vc_stats.rating_count, 0),
  vp.average_rating = vc_stats.avg_rating;


COMMIT;
